resources.string:
#include <SoftwareSerial.h>

SoftwareSerial bluetooth(10,11);

void setup()
 {
 bluetooth.begin(9600);
 }

void loop()
 {
 int incomingByte = 0;
 String content = "";
 char character;

 while(bluetooth.available())
 {
 character = bluetooth.read();
 content.concat(character);
 }

 if (content!="")
 {
 bluetooth.print(content);
 }
 delay(100);
 }

/*

IMPORTANT: The LED on the HC-05 should blink at 2-second intervals. This shows that the HC-05 is in AT mode.

----------------------------------------------------------------
AT COMMAND LIST
----------------------------------------------------------------

AT+VERSION? Gets the HC-05 Version
AT+NAME=MYEXAMPLE Change the device name to MYEXAMPLE
AT+PSWD=5678 Change the default security code from 1234 to 5678
AT+UART=9600,1,0 Change the baud rate to 9600
AT+UART=115200,1,0 Change the baud rate to 115200

After every command you should get an OK response.

*/

#include <SoftwareSerial.h>

SoftwareSerial BTSerial(10,11);

void setup()
 {
 pinMode(9, OUTPUT);
 digitalWrite(9, HIGH); // Activating AT Mode

 Serial.begin(9600);
 BTSerial.begin(38400); // HC-05 default speed in AT command mode

 Serial.println("Enter AT commands:");
 }

void loop()
 {
 if (BTSerial.available())
 {
 Serial.write(BTSerial.read());
 }

 if (Serial.available())
 {
 BTSerial.write(Serial.read());
 }
 }
